    try:
        # Read both Excel files
        generated_df = pd.read_excel(generated_file, index_col=0)
        standard_df = pd.read_excel(standard_file, index_col=0)

        # Ensure both DataFrames have the same shape
        if generated_df.shape != standard_df.shape:
            raise ValueError(f"Error: The files have different shapes. "
                             f"Generated: {generated_df.shape}, Standard: {standard_df.shape}")

        # Compare each cell
        for row in generated_df.index:
            for col in generated_df.columns:
                gen_value = generated_df.at[row, col]
                std_value = standard_df.at[row, col]
                if pd.isna(gen_value) and pd.isna(std_value):
                    continue
                if gen_value != std_value:
                    raise ValueError(f"Mismatch found at cell ({row}, {col}): "
                                     f"Generated value = '{gen_value}', Standard value = '{std_value}'")
    
    except Exception as e:
        raise e

