function copyToClipboard() {
    // Placeholder function for copying password to clipboard
    var password = document.getElementById('password').innerText;
    navigator.clipboard.writeText(password).then(function () {
        // Show a message on the page instead of using alert
        var message = document.getElementById('copy-message');
        message.innerText = 'Password copied to clipboard!';
        message.style.display = 'block';  // Make the message visible
        // Hide the message after 3 seconds
        setTimeout(function () {
            message.style.display = 'none';
        }, 3000);
    }, function (err) {
        // In case of error (e.g., clipboard API not supported)
        console.error('Could not copy text: ', err);
    });
}

