import random
import json
import os

def clear_screen():
    os.system('cls' if os.name == 'nt' else 'clear')

def display_page(page_data, current_location):
    clear_screen()
    print(page_data[current_location]["text"])


def get_player_input(options):
    while True:
        player_input = input("> ").lower()
        if player_input in options:
            return player_input
        else:
            print("Invalid input. Please try again.")


def combat(player_health, enemy_health, enemy_name):
    while player_health > 0 and enemy_health > 0:
        print(f"You attack the {enemy_name}!")
        damage = random.randint(1, 5)
        enemy_health -= damage
        print(f"You deal {damage} damage. The {enemy_name} has {enemy_health} health remaining.")

        if enemy_health <= 0:
            print(f"You defeated the {enemy_name}!")
            return player_health

        print(f"The {enemy_name} attacks you!")
        damage = random.randint(1, 3)
        player_health -= damage
        print(f"You take {damage} damage. You have {player_health} health remaining.")

    if player_health <= 0:
        print("You have been defeated...")
        return 0

    return player_health


def game():
    player_health = 20
    current_location = "start"

    pages = {
        "start": {
            "text": "You find yourself in a dark forest. There is a path to the north and east.",
            "options": {"north": "cabin", "east": "goblin"},
            "enemy": None
        },
        "cabin": {
            "text": "You enter a small, abandoned cabin. You find a healing potion.  There's a path back south.",
            "options": {"south": "start"},
             "enemy": None,
            "heal": 5

        },

        "goblin": {
            "text": "You encounter a goblin! Prepare for battle!  Path leads west.",
            "options": {"west": "start"},
            "enemy": {"name": "goblin", "health": 10}
        }
    }


    while True:

        display_page(pages, current_location)

        if pages[current_location].get("heal"):
             player_health += pages[current_location]["heal"]
             print(f"You drink the healing potion. You now have {player_health} health.")
             pages[current_location].pop("heal") # remove potion after use


        if pages[current_location].get("enemy"):
            enemy = pages[current_location]["enemy"]
            player_health = combat(player_health, enemy["health"], enemy["name"])
            if player_health <= 0:
                break  # Game over
            pages[current_location].pop("enemy")  # remove enemy after fight

        if player_health <= 0:  # Check health after potential combat
             break



        available_options = pages[current_location]["options"]
        print("Available options:", ", ".join(available_options.keys()))

        player_input = get_player_input(available_options.keys())
        current_location = available_options[player_input]


    print("Game Over.")


if __name__ == "__main__":

    function_checklist = [
    {"page":"start", "function":[{"function":"north", "description": "Go to the cabin."}, {"function":"east", "description": "Encounter the goblin."}]},
    {"page":"cabin", "function":[{"function":"south", "description": "Go back to the start."}, {"function":"drink potion", "description": "Heal yourself."}]},
    {"page":"goblin", "function":[{"function":"west", "description": "Go back to the start."}, {"function":"fight", "description": "Battle the goblin."}]}
]


    print(json.dumps(function_checklist, indent=4))  # Print the JSON checklist

    game()

