import random
import json

def create_character():
    name = input("Enter your character's name: ")
    return {"name": name, "health": 100, "attack": 10, "gold": 0, "inventory": []}

def display_status(player):
    print(f"Name: {player['name']}")
    print(f"Health: {player['health']}")
    print(f"Attack: {player['attack']}")
    print(f"Gold: {player['gold']}")
    print(f"Inventory: {player['inventory']}")

def combat(player, enemy):
    print(f"You encounter a {enemy['name']}!")
    while player['health'] > 0 and enemy['health'] > 0:
        action = input("Attack or Flee? (a/f): ").lower()
        if action == 'a':
            player_damage = random.randint(1, player['attack'])
            enemy['health'] -= player_damage
            print(f"You hit the {enemy['name']} for {player_damage} damage!")
            if enemy['health'] <= 0:
                print(f"You defeated the {enemy['name']}!")
                player['gold'] += enemy['gold']
                print(f"You found {enemy['gold']} gold!")
                if "item" in enemy:
                    player["inventory"].append(enemy["item"])
                    print(f"You found a {enemy['item']}!")

                return
            enemy_damage = random.randint(1, enemy['attack'])
            player['health'] -= enemy_damage
            print(f"The {enemy['name']} hits you for {enemy_damage} damage!")
            if player['health'] <= 0:
                print("You have been defeated...")
                return
        elif action == 'f':
            print("You successfully fled!")
            return
        else:
            print("Invalid action. Please choose 'a' or 'f'.")

def game():

    functions_json = """
[
    {"page": "Character Creation", "function": [{"function": "Enter Name", "description": "Type your character's name and press Enter."}]},
    {"page": "Combat", "function": [{"function": "Attack (a)", "description": "Initiate an attack against the enemy."}, {"function": "Flee (f)", "description": "Attempt to escape from the combat encounter."}]}
]
    """

    print(functions_json)


    player = create_character()
    enemies = [
        {"name": "Goblin", "health": 20, "attack": 5, "gold": 5},
        {"name": "Orc", "health": 30, "attack": 10, "gold": 10, "item":"Sword"},
        {"name": "Troll", "health": 50, "attack": 15, "gold": 15, "item":"Shield"}
    ]

    while player['health'] > 0:
        display_status(player)
        combat(player, random.choice(enemies))

if __name__ == "__main__":
    game()
