@echo off
setlocal EnableDelayedExpansion

:: Set input and output file paths
set "inputFile=Spring Semester Subjects.xlsx"
set "outputFile=SubjectTable.xlsx"

:: Check if input file exists
if not exist "%inputFile%" (
  echo Input file not found: %inputFile%
  pause
  exit /b 1
)

:: Create a temporary CSV file
set "csvFile=temp.csv"
for /f "skip=1 tokens=2-4 delims=," %%a in ('cscript //nologo excel_to_csv.vbs "%inputFile%"') do (
  echo %%a,%%b,%%c>>"%csvFile%"
)


:: Create a two-dimensional subject table
(
  echo Weekday,First period,Second period,Third period,Fourth period
  for %%a in (Monday Tuesday Wednesday Thursday Friday Saturday Sunday) do (
    set "line=%%a"
    for %%b in (First Second Third Fourth) do (
      set "found="
      for /f "tokens=1-3 delims=," %%c in (%csvFile%) do (
        if "%%c"=="%%a" if "%%d"=="%%b period" (
          set "found=%%e"
          goto :break
        )
      )
      :break
      if defined found (
        set "line=!line!,!found!"
      ) else (
        set "line=!line!,"
      )

    )
    echo !line!
  )
)> output.csv

:: Convert CSV to XLSX using a suitable tool (e.g., csvkit, PowerShell, etc.)
:: Example using PowerShell:
powershell -Command "Import-Csv -Path '.\\output.csv' | Export-Excel -Path '%outputFile%' -WorksheetName 'Sheet1'"


:: Delete temporary files
del "%csvFile%"
del output.csv


echo Subject table created successfully: %outputFile%
pause
exit /b 0


:: --- excel_to_csv.vbs --- (Save this as a separate .vbs file)
Set objExcel = CreateObject("Excel.Application")
Set objWorkbook = objExcel.Workbooks.Open(WScript.Arguments(0))
Set objWorksheet = objWorkbook.Sheets(1) 

objWorksheet.SaveAs "temp.csv", 6 ' xlCSV

objWorkbook.Close False
objExcel.Quit
Set objWorksheet = Nothing
Set objWorkbook = Nothing
Set objExcel = Nothing
