import random
import json

def show_intro():
    print("Welcome to the Python Bashcrawl!")
    print("You find yourself in a dark dungeon...")

def create_player():
    player = {"name": input("Enter your name: "), "health": 100, "attack": 10, "gold": 0}
    return player

def create_monster():
    monster_names = ["Goblin", "Orc", "Troll", "Skeleton"]
    monster_name = random.choice(monster_names)
    monster = {"name": monster_name, "health": random.randint(20, 50), "attack": random.randint(5, 15)}
    return monster

def combat(player, monster):
    print(f"You encountered a {monster['name']}!")
    while player["health"] > 0 and monster["health"] > 0:
        print(f"Your health: {player['health']}, {monster['name']}'s health: {monster['health']}")
        action = input("Attack or Flee? (a/f): ").lower()
        if action == "a":
            monster["health"] -= player["attack"]
            print(f"You hit the {monster['name']} for {player['attack']} damage!")
            if monster["health"] <= 0:
                print(f"You defeated the {monster['name']}!")
                player["gold"] += random.randint(10, 30)
                print(f"You found {player['gold']} gold!")
                break
            player["health"] -= monster["attack"]
            print(f"The {monster['name']} hit you for {monster['attack']} damage!")
        elif action == "f":
            print("You fled the battle!")
            break
        else:
            print("Invalid action!")
    if player["health"] <= 0:
        print("You died!")


def game_loop(player):
    while player["health"] > 0:
         combat(player, create_monster())
         if player["health"] <=0:
             break
         continue_playing = input("Continue exploring? (y/n): ").lower()
         if continue_playing != "y":
             break
    print("Game Over")


def generate_function_checklist():

    checklist = [
        {"page": "Intro", "function": [{"function": "show_intro", "description": "Displays the game introduction."}]},
        {"page": "Character Creation", "function": [{"function": "create_player", "description": "Creates the player character."}]},
        {"page": "Monster Generation", "function": [{"function": "create_monster", "description": "Creates a random monster."}]},
        {"page": "Combat", "function": [{"function": "combat", "description": "Handles the combat between the player and a monster."}]},
        {"page": "Game Loop", "function": [{"function": "game_loop", "description": "Runs the main game loop."}]}
    ]
    return json.dumps(checklist, indent=4)






if __name__ == "__main__":
    show_intro()
    player = create_player()
    game_loop(player)
    print(generate_function_checklist())
