   response = requests.get(api_url)

   if response.status_code == 200:
       data = response.json()
       rates = data['rates']
   else:
       rates = {'error': 'Unable to fetch rates' }

    return render(request,'exchange_rate_trends.html',{'rates': rates})

def home(request): 
    latest_data = CurrencyData.objects.order_by('-date').first()
   if latest_data:
      context = {'rates': latest_data.from_to_list(), 'last_updated': latest_data.date}
   else:  
      context = {'rates': [] , 'last_updated': ''} 
    return render(request, 'home.html', context)

