from django.http import HttpResponse
import markdown

def index(request):
    return render(request, 'templates/home.html')
def convert_markdown(request):
    if request.method == 'POST':
        markdown_content = request.POST.get('markdown-input')
        html_output = markdown.markdown(markdown_content)
        entry = MarkdownEntry(content=markdown_content, html_output=html_output)
        entry.save()
        return HttpResponse(html_output)
    return render(request, 'templates/home.html')

