from django.shortcuts import render, redirect, get_object_or_404
from .models import TodoList, Task

# Home View
def view_dashboard(request):
    todo_lists = TodoList.objects.all()
    return render(request, 'home.html', {'todo_lists': todo_lists})

# TodoList Views
def create_new_list(request):
    if request.method == 'POST':
        name = request.POST['name']
        TodoList.objects.create(name=name)
    return redirect('home')

def view_tasks(request, list_id):
    todo_list = get_object_or_404(TodoList, pk=list_id)
    return render(request, 'todo_list.html', {'todo_list': todo_list})

# Task Views
def add_new_task(request, list_id):
    if request.method == 'POST':
        name = request.POST['name']
        list = get_object_or_404(TodoList, pk=list_id)
        Task.objects.create(name=name, list=list)
    return redirect('view_tasks', list_id=list_id)

def edit_task(request, task_id):
    task = get_object_or_404(Task, pk=task_id)
    if request.method == 'POST':
        task.name = request.POST['name']
        task.save()
        return redirect('view_tasks', list_id=task.list.id)
    return render(request, 'edit_task.html', {'task': task})

def delete_task(request, task_id):
    task = get_object_or_404(Task, pk=task_id)
    task.delete()
    return redirect('view_tasks', list_id=task.list.id)

def mark_task_as_completed(request, task_id):
    task = get_object_or_404(Task, pk=task_id)
    task.is_completed = True
    task.save()
    return redirect('view_tasks', list_id=task.list.id)

