import random
import string

from django.shortcuts import render
from django.http import HttpResponse


def generate_password(length=12, include_uppercase=True, include_lowercase=True, include_numbers=True, include_symbols=True):
    """
    Generates a random password based on the specified criteria.
    """
    characters = ''
    if include_uppercase:
        characters += string.ascii_uppercase
    if include_lowercase:
        characters += string.ascii_lowercase
    if include_numbers:
        characters += string.digits
    if include_symbols:
        characters += string.punctuation
    if not characters:
        return '' # Empty if no criteria given
    return ''.join(random.choice(characters) for _ in range(length))


def password_generator_view(request):
    """
    Handles the password generation form and renders the result.
    """
    context = {}
    if request.method == 'GET':
        length = int(request.GET.get('length', 12))
        include_uppercase = 'uppercase' in request.GET
        include_lowercase = 'lowercase' in request.GET
        include_numbers = 'numbers' in request.GET
        include_symbols = 'symbols' in request.GET

        password = generate_password(length, include_uppercase, include_lowercase, include_numbers, include_symbols)
        context = {
            'password': password,
            'length': length,
            'include_uppercase': include_uppercase,
            'include_lowercase': include_lowercase,
            'include_numbers': include_numbers,
            'include_symbols': include_symbols
        }

    return render(request, 'generator.html', context)


