```batch
@echo off

:: Read excel function
:read_excel
set /p x=Reading data from Spring Semester Subjects.xlsx...
copy Spring\\Semester\\Subjects.xlsx result.csv
echo Data read successfully!
goto menu

:: Convert to table function
:convert_to_table
for /f "tokens=1-2 delims=:" %%a in (result.csv) do (
    set /a num=%%b
    if defined time_%%a (
        echo %%a,%%b,%%a >> output.csv
    ) else (
        echo %%a,%%b,, >> output.csv
    )
)
echo Data converted successfully!

:: Output to excel function
:output_to_excel
copy result.csv Input.xlsx
convert_to_table
start "Excel" excel.exe /e Input.xlsx
echo Data written to Output.xlsx

:menu
cls
echo Menu:
echo 1. Read Excel
echo 2. Convert to Table
echo 3. Output to Excel
set /p choice=Enter your choice:

if %choice%==1 goto read_excel
if %choice%==2 goto convert_to_table
if %choice%==3 (
    goto output_to_excel
)
goto menu
