from django.shortcuts import render
import json
from ai_chatbot_project.models import AiChatBotModel
# Create your views here.
def chatbot_response(request):
    if request.method == 'POST':
        user_input = request.POST.get('user_input')
        response = {}
        # Add logic to respond based on the user's input
        model = AiChatBotModel()
        for question in model.questions:
            if question == user_input:
                response['message'] = 'That is a very wise question...' + model[question] + ' ' + question[:5] + '...'
        # Use template engine to display the JSON structure
        return render(request, 'response.json', {'json_data': json.dumps(response)})
    else:
        return render(request, 'index.html')
def home_view(request):
    return render(request, 'index.html')

