from django.shortcuts import render, redirect
from .models import BudgetItem, Transaction
from .forms import BudgetItemForm, TransactionForm

def budget_list(request):
    budget_items = BudgetItem.objects.filter(user=request.user)
    transactions = Transaction.objects.all()
    return render(request, 'budget_list.html', {'budget_items': budget_items, 'transactions': transactions})

def add_budget_item(request):
    if request.method == 'POST':
        form = BudgetItemForm(request.POST)
        if form.is_valid():
            form.save()
            return redirect('budget_list')
    else:
        form = BudgetItemForm()
    return render(request, 'add_budget_item.html', {'form': form})

def add_transaction(request):
    if request.method == 'POST':
        form = TransactionForm(request.POST)
        if form.is_valid():
            form.save()
            return redirect('budget_list')
    else:
        form = TransactionForm()
    return render(request, 'add_transaction.html', {'form': form})

