from django import forms
from .models import BudgetItem, Transaction

class BudgetItemForm(forms.ModelForm):
    class Meta:
        model = BudgetItem
        fields = ('name', 'amount')

class TransactionForm(forms.ModelForm):
    class Meta:
        model = Transaction
        fields = ('budget_item', 'transaction_date', 'amount')
