from django.contrib.auth.decorators import login_required
from .models import Transaction

@login_required
def home(request):
    transactions = Transaction.objects.all()
    return render(request, 'home.html', {'transactions': transactions})

@login_required
def balance_tracker(request):
    total_balance = sum([transaction.amount for transaction in Transaction.objects.all()])
    return render(request, 'balance_tracker.html', {'total_balance': total_balance})

