from django.shortcuts import render
import markdown

def home(request):
    context = {
        'welcome_message': 'Welcome to the Markdown to HTML Converter!',
        'intro_text': 'Convert your Markdown text to pretty HTML easily.'
    }
    return render(request, 'home.html', context)

def convert_page(request):
    html_output = ''
    if request.method == 'POST' and 'markdown_input' in request.POST:
        markdown_text = request.POST['markdown_input']
        html_output = markdown.markdown(markdown_text)

    return render(request, 'convert.html', {'html_output': html_output})

def about_page(request):
    context = {
        'team_info': 'Developed by the Web Development Team.',
        'faq': 'Common questions can be found in our FAQ section.'
    }
